            id: 5,
            name: 'Hygiene Hose (shattafe)',
            check: false
        }, {
            id: 6,
            name: 'Joint kitchen',
            check: false

        }, {
            id: 7,
            name: 'Guests allowed',
            check: false
        }, {
            id: 8,
            name: 'Sleepovers',
            check: false

        }, {
            id: 9,
            name: 'Pet friendly',
            check: false

        }, {
            id: 10,
            name: '24/7 security',
            check: false

        }, {
            id: 11,
            name: 'Laundry',
            check: false

        },
        {
            id: 12,
            name: 'Electricity 24/7',
            check: false

        }, {
            id: 13,
            name: 'AC',
            check: false

        }, {
            id: 14,
            name: 'Chauffage',
            check: false

        },

    ]);
    const handleCheck = (id) => {
        setCheckboxs((prev) => {
            return prev.map((item) => {
                if (item.id === id) {
                    return { ...item, check: !item.check };
                } else {
                    return { ...item };
                }
            });
        });
    };
    const handleChecktype = (id) => {
        setCheckboxstype((prev) => {
            return prev.map((item) => {
                if (item.id === id) {
                    return { ...item, check: !item.check };
                } else {
                    return { ...item };
                }
            });
        });
    };
    const rendertype = () => {
        return checkboxstype.map(item => (
            <div className='pr chich inpt'>
                <div className="inppr" key={item.id} onClick={() => handleChecktype(item.id)} >
                    <input type="checkbox" name={item.name} id={item.id} value={item.check} />
                    {item.check && (<>
                        <div className="before"></div>
                        <div className="after">‚úî</div>

                    </>
                    )}
                </div>
                <label htmlFor={item.id}>{item.name}</label>
            </div>
        ));

    }
    const renderCheckboxs = () => {
        return checkboxs.map(item => (
            <div className='pr chich inpt'>
                <div className="inppr" key={item.id} onClick={() => handleCheck(item.id)} >
                    <input type="checkbox" name={item.name} id={item.id} value={item.check} />
                    {item.check && (<>
                        <div className="before"></div>
                        <div className="after">‚úî</div>

                    </>
                    )}
                </div>
                <label htmlFor={item.id}>{item.name}</label>
