import React, { useState } from "react";
import "./SearchBar.css";
import SearchIcon from "@material-ui/icons/Search";

function SearchBar({ placeholder, data }) {
  const [filteredData, setFilteredData] = useState([]);
  const [wordEntered, setWordEntered] = useState("");

  const handleFilter = (event) => {
    const searchWord = event.target.value;
    setWordEntered(searchWord);
    const newFilter = data.filter((value) => {
      return value.title.toLowerCase().includes(searchWord.toLowerCase());
    });

    if (searchWord === "") {
      setFilteredData([]);
    } else if (searchWord.length > 1) {
      const x = { "title": "sdsdldf" };
      const finalfilter = [...newFilter, x]

      setFilteredData(finalfilter);
    } else {
      setFilteredData([]);
    }
  };



  return (
    <div className="bg container mt-6 nav-div">
      <div className="HeaderText">
        Finding a dorm just got easier!!!!!!!!!!!!!!!!!
      </div>
      <div>
        <div className="row d-flex justify-content-center align-items-center searchbar">
          <div className="col-md-5 navb ">
            <div className="form">
              <i className="fa fa-search"></i>
              <input
                type="text"
                className="form-control form-input shadow-none rounded-1 "
                placeholder={placeholder}
                value={wordEntered}
                onChange={handleFilter}
              />
            </div>
          </div>


        </div>
      </div>

      {filteredData.length != 0 && (
        <div className="row d-flex justify-content-center align-items-center">
          <div className="col-md-5 ">
            <div className="dataResult ">
              {filteredData.map((value, key) => {
                return (
                  <a className="dataItem" href={value.link} target="_blank">
                    <p>{value.title} </p>
                  </a>
                );
              })}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default SearchBar;
