import React, { useRef, useEffect, useCallback, useState } from 'react'
import Footer from '../../components/footer/Footer'
import Navbar from '../../components/navbar/Navbar'
import "./AddDorm.css"
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup"
import * as yup from "yup";
import ReactMapGL, {
    Marker,

} from "react-map-gl"
import mapboxgl from "mapbox-gl";
const schema = yup.object().shape({
    DormName: yup.string().required('This field is required').min(3).max(50),
    Price: yup.number().required().test(
        'no-leading-zero',
        'Leading zero is not allowed',
        (value, context) => {
            return context.originalValue && !context.originalValue.startsWith('0');
        }
    ),


})

mapboxgl.accessToken = "pk.eyJ1Ijoid2FmaWNraCIsImEiOiJjbGNuZ2ttcnoxMm83M25zOTM0cGpkanlmIn0.8U0pDk8RKlhvQrxzgWsbxQ"
function AddDorm() {
    const { register, handleSubmit, errors } = useForm({
        resolver: yupResolver(schema),


    });

    const submitForm = (data) => {
        const obj = { ...data, long: long.current, lat: lat.current }
        console.log(obj);


    };
    const [Long, setLong] = useState("");
    const [Lat, setLat] = useState("");



    const mapContainerRef = useRef(null);
    const lat = useRef(null);

    const long = useRef(null);



    useEffect(() => {
        const map = new mapboxgl.Map({
            container: mapContainerRef.current,
            style: "mapbox://styles/mapbox/streets-v11?optimize=true",
            center: [35.478235, 33.892371],
            zoom: 15.9,
            maxZoom: 19,

        });


        if (mapContainerRef.current.style.visibility === true) map.resize();

        let mapMarkers = []



        map.on('click', (e) => {
            mapMarkers.forEach((marker) => marker.remove())
            const marker = new mapboxgl.Marker({ color: "#562F4A" });
            marker.setLngLat(e.lngLat).addTo(map);
            mapMarkers.push(marker)
            console.log("lng:" + marker._lngLat.lng + "     Lat:" + marker._lngLat.lat);
            long.current = marker._lngLat.lng
            lat.current = marker._lngLat.lat




            console.log(lat)
        });

    }, []);
    const [checkboxs,setCheckboxs]=useState([
        {
            id:1,
            name:'Cleaning',
            check:false
        },{
            id:2,
            name:'wifi',
            check: false

        }
    ]);
    const handleCheck=(id)=>{
        setCheckboxs((prev)=>{
            return prev.map((item)=>{
                if(item.id===id){
                    return {...item, check:!item.check};
                }else{
                    return {...item};
                }
            });
        });
    };
    const renderCheckboxs=()=>{
        return checkboxs.map(item=>(
              <div className = "inpt" key={item.id} value={item.check}onClick={()=>handleCheck(item.id)} >
                <input type="checkbox"  name="Cleaning" id="Cleaning" />
                <label htmlFor="Cleaning">{item.name}</label>
                                </div>
        
        ));
    };

    const renderfilters=()=>{
        return checkboxs.map((item)=>{
            if (item.check){
                return (
                    <span onClick={() => handleCheck(item.id)} className='sax' key={item.id}>{item.name} âœ—</span>
                );
            }else{
                return null;
            }
        })
    }


    return (
        <span className='bud'>

            <Navbar />
            <div className="bod">
                <div className='card card7'>
                    <form action="" onSubmit={handleSubmit(submitForm)} >
                        <div className='head'>
                            <h1>Add a dorm</h1>
                            <p>Enter the full name, contract information, Location, and image/s of the dorm you would like to add.</p>
                        </div>
                        <div className="inp headm">
                            <h4>Dorme name</h4>
                            <input type="text" autocomplete="off" name="DormName" id="Dormname" placeholder='Dorm name' className='form-control form-input shadow-none rounded-3 pad' ref={register} />
                            <span className='error'>{errors.DormName?.message}</span>

                        </div>
                        <div className="contractInfo">
                            <h3>Contract Information</h3>
                            <div className="flexxx">
                                <div className="inp prices1">
                                    <h4>Rent per month ($) </h4>

                                    <input type="text" name="Price" autocomplete="off" placeholder='$' id="Price" className='form-control form-input shadow-none rounded-3 pad' ref={register} />
                                    <span className='error'>{errors.Price?.message ? "Price must be a valid Number" : ""}</span>

                                </div>
                                <div className="inp prices1">
                                    <h4>Deposit ($) </h4>

                                    <input type="text" name="Price" autocomplete="off" placeholder='$' id="Price" className='form-control form-input shadow-none rounded-3 pad' ref={register} />
                                    <span className='error'>{errors.Price?.message ? "Price must be a valid Number" : ""}</span>

                                </div>
                                <div className="inp prices1">
                                    <h4>Contract length (in months) </h4>

                                    <input type="text" name="Price" autocomplete="off" placeholder='' id="Price" className='form-control form-input shadow-none rounded-3 pad' ref={register} />
                                    <span className='error'>{errors.Price?.message ? "Price must be a valid Number" : ""}</span>

                                </div>
                            </div>
                            <div className="addinfo inp ">
                                <h4>Amenities (features)</h4>
                                <div className="filters">
                                    {renderfilters
                                    ()}
                                </div>
                                <div className="Check">
                                    {renderCheckboxs()}
                                </div>
                                <div className="inpt">
                                    <input type="checkbox" name="Cleaning" id="Cleaning" />
                                    <label htmlFor="Cleaning">Cleaning service</label>
                                </div>
                                <div className="inpt">
                                    <input type="checkbox" name="wifi" id="wifi" />
                                    <label htmlFor="wifi">Unlimited Wifi</label>
                                </div>
                                <div className="inpt">
                                    <input type="checkbox" name="lounge" id="lounge" />
                                    <label htmlFor="lounge">Shared lounge</label>
                                </div>
                                <div className="inpt">
                                    <input type="checkbox" name="water" id="water" />
                                    <label htmlFor="water">Hot water</label>
                                </div>
                                <div className="inpt">
                                    <input type="checkbox" name="shattafe" id="shattafe" />
                                    <label htmlFor="shattafe">Hygiene Hose (shattafe)</label>
                                </div>
                                <div className="inpt">
                                    <input type="checkbox" name="kitchen" id="kitchen" />
                                    <label htmlFor="kitchen">Joint kitchen</label>
                                </div>
                                <div className="inpt">
                                    <input type="checkbox" name="guests" id="guests" />
                                    <label htmlFor="guests">Guests allowed</label>
                                </div>
                                <div className="inpt">
                                    <input type="checkbox" name="sleepovers" id="sleepovers" />
                                    <label htmlFor="sleepovers">Sleepovers</label>
                                </div>
                                <div className="inpt">
                                    <input type="checkbox" name="guests" id="guests" />
                                    <label htmlFor="guests">Guests allowed</label>
                                </div>
                                <div className="inpt">
                                    <input type="checkbox" name="security" id="security" />
                                    <label htmlFor="security">24/7 security</label>
                                </div>
                                <div className="inpt">
                                    <input type="checkbox" name="laudry" id="laudry" />
                                    <label htmlFor="laundry">Laudry</label>
                                </div>
                            </div>
                            <div className="inpt">
                                    <h4>Dorm Type</h4>
                                    
                                </div>
                            <div className="addinfo inp prices1">
                                <h4>Additional Information (optional) </h4>

                                <textarea id="w3review" name="w3review" rows="3" cols="50" style={{ padding: 10 }}>

                                </textarea>
                            </div>




                        </div>

                        <div className="pics">
                            <h3>Add Your very cool dorm Pics</h3>
                            <input type="file" id="myFile" name="filename" multiple></input>
                        </div>
                        <div className="location">
                            <h4>Please Select the exact location of your dorm.</h4>
                            <div className="Map" ref={mapContainerRef} >



                            </div>

                        </div>
                        <input type="hidden" id="long" name="long" value={long.current} />

                        <input type="hidden" id="Lat" name="Lat" value={lat.current} />

                        <div className="buttons">
                            <button className='back'>&laquo; Back</button>
                            <button type='submit' className='continue'>Submit &raquo;</button>

                        </div>
                    </form>
                </div>
            </div>
        </span>
    )
}

export default AddDorm
