import React, { useState } from "react";
import "./SearchBar.css";
import SearchIcon from "@material-ui/icons/Search";

function SearchBar({ placeholder, data }) {
  const [filteredData, setFilteredData] = useState([]);
  const [wordEntered, setWordEntered] = useState("");

  const handleFilter = (event) => {
    const searchWord = event.target.value;
    setWordEntered(searchWord);
    const newFilter = data.filter((value) => {
      return value.title.toLowerCase().includes(searchWord.toLowerCase());
    });


    if (searchWord === "") {
      setFilteredData([]);
    } else if (searchWord.length > 1) {
      const y = newFilter.length;
      const x = { "title": ".." };
      if (y > 0) {
        x.title = `${y} Results shown`
      }
      else {
        x.title = `${y} Results shown :(`

      }

      const finalfilter = [...newFilter, x]


      setFilteredData(finalfilter);
    } else {
      setFilteredData([]);
    }
  };





  return (
    <div className="bg container mt-6 nav-div">
      <div className="HeaderText">
      </div>
      <div>
        <div className="row d-flex justify-content-center align-items-center searchbar" style={{ marginLeft: 1, marginRight: 0, marginBottom: 0 }}>
          <div className="Head">
            <h1>Finding a Dorm just got Easier</h1>
          </div>
          <div className="col-md-6 navb " >
            <div className="form">
              <i className="fa fa-search"></i>
              <input
                type="text"
                className="form-control form-input shadow-none rounded-0 padman"
                placeholder={placeholder}
                value={wordEntered}
                onChange={handleFilter}
              />

            </div>
          </div>


        </div>
      </div>

      {filteredData.length != 0 && (
        <div className="row d-flex justify-content-center align-items-center" style={{ marginLeft: 0, marginRight: 0, marginBottom: 15 }}>
          <div className="col-md-6">
            <div className="dataResult ">
              {filteredData.map((value, key) => {
                return (
                  <a className="dataItem" href={value.link} target="_blank">
                    <p>{value.title} </p>
                  </a>
                );
              })}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default SearchBar;
