import React, { useRef, useEffect, useCallback, useState } from 'react'
import Footer from '../../components/footer/Footer'
import Navbar from '../../components/navbar/Navbar'
import "./AddDorm.css"
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup"
import * as yup from "yup";
import ReactMapGL, {
    Marker,

} from "react-map-gl"
import mapboxgl from "mapbox-gl";
const schema = yup.object().shape({
    DormName: yup.string().min(3).max(90)
        .required('This field is required'),
})

mapboxgl.accessToken = "pk.eyJ1Ijoid2FmaWNraCIsImEiOiJjbGNuZ2ttcnoxMm83M25zOTM0cGpkanlmIn0.8U0pDk8RKlhvQrxzgWsbxQ"
function AddDorm() {
    const { register, handleSubmit, errors } = useForm({
        resolver: yupResolver(schema),


    });

    const submitForm = (data) => {
        const obj={...data,long:long.current,lat:lat.current}
        console.log(obj);


    };

    const mapContainerRef = useRef(null);
    const lat = useRef(null);

    const long = useRef(null);
    



    return (
        <span className='bud'>

            <Navbar />
            <div className="bod">
                <div className='card card7'>
                    <form action="" onSubmit={handleSubmit(submitForm)} >
                        <div className='head'>
                            <h1>Add a dorm</h1>
                            <p>Enter the full name and Location of the dorm  you would like to add.</p>
                        </div>
                        <div className="inp">
                            <h4>Dorme name</h4>
                            <input type="text" name="DormName" id="Dormname" placeholder='Dorm name' className='form-control form-input shadow-none rounded-3 pad' ref={register} />

                            <span className='error'>{errors.DormName?.message}</span>

                        </div>
                        <div className="location">
                            <h4>Please Select the exact location of your dorm.</h4>
                            <div className="Map" ref={mapContainerRef} >



                            </div>

                        </div>
                        <input type="hidden" id="long" name="long" value={long.current} />

                        <input type="hidden" id="Lat" name="Lat" value={lat.current} />
                        <div className="buttons">
                            <button className='back'>&laquo; Back</button>
                            <button type='submit' className='continue'>Continue &raquo;</button>

                        </div>
                    </form>
                </div>
            </div>
        </span>
    )
}

export default AddDorm
