import React, { useEffect, useRef, useState } from 'react'


import Navbar from '../../components/navbar/Navbar'
import './Dorms.css';
import { useSelector, useDispatch } from 'react-redux'
import { useNavigate, useParams } from "react-router-dom";
import { getDormById } from '../../actions/dorms';
import mapboxgl from "mapbox-gl";
import { BsHouseFill } from "react-icons/bs";

import '../School/stars.css'

mapboxgl.accessToken = "pk.eyJ1Ijoid2FmaWNraCIsImEiOiJjbGNuZ2ttcnoxMm83M25zOTM0cGpkanlmIn0.8U0pDk8RKlhvQrxzgWsbxQ"

function Dorms() {
  const navigate = useNavigate();
  const dispatch = useDispatch()
  let { id } = useParams();
  console.log(id)
  const dorms = useSelector((state) => state.dorms)
  console.log(dorms)
  let long = dorms.Coordinates?.long
  console.log(dorms.Coordinates?.lag)
  const mapContainerRef = useRef(null);

  useEffect(() => {
    dispatch(getDormById(id));
    const map = new mapboxgl.Map({
      container: mapContainerRef.current,
      style: "mapbox://styles/mapbox/streets-v11?optimize=true",
      //long then lat
      center: [35.4782334668392, 33.892656396967425 ],
      zoom: 15.9,
      maxZoom: 19,
      pitch: 60
    });


    if (mapContainerRef.current.style.visibility === true) map.resize();

    let mapMarkers = []

  }, [dispatch]);
  return (

    <div>
      <Navbar />
      {/* <center>
        <h1>ボールを食べる</h1>
        <h2>INSANELY COOL DORM CHIEF</h2>
        <p>Plan:</p>
        <ul>
          <li>Dorm info (photo rating...) on the left</li>
          <li>Reviews of the dorm on the right</li>
          Get dorm from id that is in the url
        </ul>

      </center> */}

      <div className='headersi'><h1 ><BsHouseFill style={{ width: "30px", color: "#562F4A" }} /><strong>  {dorms.Name}</strong> reviews  </h1></div>
      <center>
      </center>
      <div className="Flexing">
        <div className="sectionA">
          <img src={dorms.images} alt="BIGNONON" />
          <div className="sa2">
          <div className="rate">
          <h3>Overall Rating</h3>
          <p class="starability-result" data-rating={dorms.Rating}>
              </p><h4></h4>{dorms.Rating} </div>
            <div className="Type">
              <h3>Dorm Type</h3>
              {dorms.contractInformation?.DormType?.map((value, key) => {
                return (
                  <span className='wab rounded-4' key={value.id}>{value}</span>
                )
              })}
            </div>
            <div className="Amenitiess">
              <h3>Amenities</h3>
              {dorms.contractInformation?.Amenities?.map((value,key)=>{
                return(
                  <span className='wab rounded-4' key={value.id}>{value}</span>
                )
              })}
            </div>
              <div className="Map" ref={mapContainerRef} > </div>
          </div>
        </div>

      </div>
    </div>
  )
}
export default Dorms
