import React from 'react'
import Footer from '../../components/footer/Footer'
import Navbar from '../../components/navbar/Navbar'
import "./AddDorm.css"
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup"
import * as yup from "yup";
import ReactMapGL from "react-map-gl"
import { useState } from 'react';
const schema = yup.object().shape({
    DormName: yup.string().min(3).max(90)
        .required('This field is required')
})


function AddDorm() {

    const { register, handleSubmit, errors } = useForm({
        resolver: yupResolver(schema),
    });

    const submitForm = (data) => {
        console.log(data);
    };
    let [viewport, setViewport] = useState({
        latitude: 37.7577,
        longitude: -122.4376,
        zoom: 8
    });

    return (
        <span className='bud'>
            <Navbar />
            <div className="bod">
                <div className='card card7'>
                    <form action="" onSubmit={handleSubmit(submitForm)}>
                        <div className='head'>
                            <h1>Add a dorm</h1>
                            <p>Enter the full name and Location of the dorm  you would like to add.</p>
                        </div>
                        <div className="inp">
                            <h4>Dorme name</h4>
                            <input type="text" name="DormName" id="Dormname" placeholder='Dorm name' className='form-control form-input shadow-none rounded-3 pad' ref={register} />

                            <span className='error'>{errors.DormName?.message}</span>

                        </div>
                        <div className="location">
                            <h4>Please Select the exact location of your dorm.</h4>
                            <div className="Map">

                                <ReactMapGL className=""
                                    mapboxAccessToken='pk.eyJ1Ijoid2FmaWNraCIsImEiOiJjbGNuZ2ttcnoxMm83M25zOTM0cGpkanlmIn0.8U0pDk8RKlhvQrxzgWsbxQ'
                                    {...viewport}
                                    onMove={(newView) => setViewport(newView)}
                                    style={{ width:'innerWidth' , height: 400, marginTop:70 }}
                                    mapStyle="mapbox://styles/mapbox/streets-v9"
                                />
                            </div>
                        </div>
                        <div className="buttons">
                            <button className='back'>&laquo; Back</button>
                            <button type='submit' className='continue'>Continue &raquo;</button>

                        </div>
                    </form>
                </div>
            </div>
        </span>
    )
}

export default AddDorm
