import React, { useState } from "react";
import "./SearchBar.css";
import SearchIcon from "@material-ui/icons/Search";
import { Link } from "@material-ui/core";
//https://res.cloudinary.com/do0puhubq/image/upload/v1674987376/Schools/Haigazian_zvhirl.jpg
//https://res.cloudinary.com/do0puhubq/image/upload/v1674987198/Schools/Antonine_pcccb6.jpg
//https://res.cloudinary.com/do0puhubq/image/upload/v1674986915/Schools/LAUBYBLOS_fmoitm.jpg
//https://res.cloudinary.com/do0puhubq/image/upload/v1674986680/Schools/Sagesse_nswhkd.jpg
//https://res.cloudinary.com/do0puhubq/image/upload/v1674986528/Schools/NDU_jojnce.jpg
//https://res.cloudinary.com/do0puhubq/image/upload/v1674986012/Schools/USJhome_qlcoih.jpg
//https://res.cloudinary.com/do0puhubq/image/upload/v1674985967/Schools/LAUhome_ivgge0.jpg
//https://res.cloudinary.com/do0puhubq/image/upload/v1674985967/Schools/AUBhome_gvbnvj.jpg

function SearchBar({ placeholder, data }) {
  const [filteredData, setFilteredData] = useState([]);
  const [wordEntered, setWordEntered] = useState("");

  const handleFilter = (event) => {
    const searchWord = event.target.value;
    setWordEntered(searchWord);
    const newFilter = data.filter((value) => {
      return value.Name.toLowerCase().includes(searchWord.toLowerCase());
    });


    if (searchWord === "") {
      setFilteredData([]);
    } else if (searchWord.length > 1) {
      const y = newFilter.length;
      const x = { "title": ".." };
      if (y > 0) {
        x.Name = `${y} Results shown`
      }
      else {
        x.Name = `${y} Results shown :(`

      }

      const finalfilter = [...newFilter, x]


      setFilteredData(finalfilter);
    } else {
      setFilteredData([]);
    }
  };





  return (
    <div className="bg container mt-6 nav-div">
      <div className="HeaderText">
      </div>
      <div>
        <div className="row d-flex justify-content-center align-items-center searchbar" style={{ marginLeft: 0, marginRight: 1, marginBottom: 0 }}>
          <div className="Head">
          <center>
            <h1>Finding a Dorm just got Easier</h1></center>
          </div>
          <div className="col-md-6 navb " >
            <div className="form">
              <i className="fa fa-search"></i>
              <input
                type="text"
                className="form-control form-input shadow-none rounded-0 padman"
                placeholder={placeholder}
                value={wordEntered}
                onChange={handleFilter}
              />

            </div>
          </div>


        </div>
      </div>

      {filteredData.length != 0 && (
        <div className="row d-flex justify-content-center align-items-center" style={{ marginLeft: 0, marginRight: 1, marginBottom: 15 }}>
          <div className="col-md-6">
            <div className="dataResult ">
              {filteredData.map((value, key) => {
                return (

                  <a  className="dataItem" key={value._id} target="_blank"> 
                  <Link to style={{ color: 'inherit', textDecoration: 'inherit' }}>
                    <p>{value.Name} <span className="place">   {value.City}</span> </p>
                  </Link>
                  </a>
                );
              })}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default SearchBar;
