import React, { useEffect, useRef, useState } from 'react'


import Navbar from '../../components/navbar/Navbar'
import './Dorms.css';
import { useSelector, useDispatch } from 'react-redux'
import { useNavigate, useParams } from "react-router-dom";
import { getDormById } from '../../actions/dorms';
import mapboxgl from "mapbox-gl";
import { BsHouseFill } from "react-icons/bs";
import { MdLocationOn } from "react-icons/md";

import '../School/stars.css'
import Footer from '../../components/footer/Footer';

mapboxgl.accessToken = "pk.eyJ1Ijoid2FmaWNraCIsImEiOiJjbGNuZ2ttcnoxMm83M25zOTM0cGpkanlmIn0.8U0pDk8RKlhvQrxzgWsbxQ"

function Dorms() {
  const navigate = useNavigate();
  const dispatch = useDispatch()
  let { id, longg, latt } = useParams();
  console.log(id)
  const dorm = useSelector((state) => state.dorms)
  const x = dorm.length
  console.log(x)
  if (x === 0) {
    navigate(`/`)
  }

  let long = dorm.Coordinates?.long
  console.log(dorm.Coordinates?.lag)
  console.log(dorm)
  const mapContainerRef = useRef(null);
  console.log(dorm.Schools?.[0])
  let nso = dorm.Schools?.[0].Name.replaceAll(' ', '_');
  console.log(dorm.length)
  useEffect(() => {
    if (id.length > 24) {
      console.log("akdjnkjdnaskdn")
    }
    dispatch(getDormById(id));
    const map = new mapboxgl.Map({
      container: mapContainerRef.current,
      style: "mapbox://styles/mapbox/streets-v11?optimize=true",
      //long then lat
      //check if >30 accept and redirect if different then
      //the dorm actual coordinates

      center: [longg, latt],
      zoom: 15.9,
      maxZoom: 19,
      pitch: 60
    });


    if (mapContainerRef.current.style.visibility === true) map.resize();
    let mapMarkers = []
  }, [dispatch]);

  const handleit = () => {
    navigate(`/`)
  }
  return (

    <div>
      <Navbar />
      {/* <center>
        <h1>ボールを食べる</h1>
        <h2>INSANELY COOL DORM CHIEF</h2>
        <p>Plan:</p>
        <ul>
          <li>Dorm info (photo rating...) on the left</li>
          <li>Reviews of the dorm on the right</li>
          Get dorm from id that is in the url
        </ul>
      </center> */}
      {dorm.length != 0 ? (<>
        <div className='headersi'><h1><BsHouseFill style={{ width: "30px", color: "#562F4A" }} /><strong>  {dorm.Name}</strong> reviews  </h1></div>
        <div className='headersi'><a href={`/dorms/${nso}`} > All {dorm.Schools?.[0].Name} Dorms</a></div>
        <center>
        </center>

      </>) : (<>


        <center className='bb'><h1>Dorm Does not exist</h1></center>
        <center><button className='backhome' onClick={() => handleit()}>Go back Home</button></center>


      </>)}
      <div className="Flexing">


        <div className="sectionA">
          {dorm.length != 0 ? (<>
            <img src={dorm?.images} alt="" />

          </>) : (<>
            <img src={dorm?.images} alt="" />


          </>)}

          <div className="sa2">
            {dorm.length != 0 ? (<>
              <div className="rate">
                <h3>Overall Rating</h3>
                <p class="starability-result" data-rating={3}>
                </p> </div>

              <div className="Prices">
                <h3>Price</h3>
                <p>{dorm.contractInformation?.Price}$ per month</p>
              </div>
              {dorm.contractInformation?.Deposit ? (
                <>
                  <div className="depos">
                    <h3>Deposit</h3>
                    <p>{dorm.contractInformation?.Deposit}$</p>
                  </div>
                </>
              ) : <>

              </>}
              {dorm.contractInformation?.ContractLength ? (
                <>
                  <div className="depos">
                    <h3>Contract length</h3>
                    <p>{dorm.contractInformation?.ContractLength} month/s</p>
                  </div>

                </>
              ) : <>

              </>}

              <div className="Type">
                <h3>Dorm Type</h3>

                {dorm.contractInformation?.DormType?.map((value, key) => {
                  return (
                    <span className=' wab rounded-4' key={value.id}>{value}</span>
                  )
                })}
              </div>
              <div className="Amenitiess">
                <h3>Amenities</h3>
                <div className='filterss'>

                  {dorm.contractInformation?.Amenities?.map((value, key) => {
                    return (
                      <span className=' wab rounded-4' key={value.id}>{value}</span>
                    )

                  })}

                </div>
              </div>
            </>
            ) : (<>



            </>)}
            {dorm.length != 0 ? (<>

              <h3 className='loll'>Location<MdLocationOn style={{ color: "aliceblue" }} /></h3>

            </>) : (<>


            </>)}
            <div className="Map" ref={mapContainerRef} > </div>



          </div>
        </div>

      </div>









      <Footer />
    </div>


  )
}
export default Dorms
