import React from 'react'
import Footer from '../../components/footer/Footer'
import Navbar from '../../components/navbar/Navbar'
import "./AddDorm.css"
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup"
import * as yup from "yup";
import ReactMapGL, {
    NavigationControl,
    FullscreenControl,
    Marker,

} from "react-map-gl"
import { useState } from 'react';
import { useCallback } from 'react';
const schema = yup.object().shape({
    DormName: yup.string().min(3).max(90)
        .required('This field is required')
})


function AddDorm() {
    const [marker, setMarker] = useState({
        latitude: 40,
        longitude: -100
    });
    const { register, handleSubmit, errors } = useForm({
        resolver: yupResolver(schema),
    });

    const submitForm = (data) => {
        console.log(data);
    };
    let [viewport, setViewport] = useState({
        latitude: 33.892371,
        longitude: 35.478235,
        zoom: 15
    });
    const add_marker = (event)=>{
        var coordinates = event.lngLat;
        console.log('Lng:', coordinates.lng, 'Lat:', coordinates.lat);
        setMarker({
            longitude: event.lngLat.lng,
            latitude: event.lngLat.lat
        });
    }

    return (
        <span className='bud'>

            <Navbar />
            <div className="bod">
                <div className='card card7'>
                    <form action="" onSubmit={handleSubmit(submitForm)}>
                        <div className='head'>
                            <h1>Add a dorm</h1>
                            <p>Enter the full name and Location of the dorm  you would like to add.</p>
                        </div>
                        <div className="inp">
                            <h4>Dorme name</h4>
                            <input type="text" name="DormName" id="Dormname" placeholder='Dorm name' className='form-control form-input shadow-none rounded-3 pad' ref={register} />

                            <span className='error'>{errors.DormName?.message}</span>

                        </div>
                        <div className="location">
                            <h4>Please Select the exact location of your dorm.</h4>
                            <div className="Map">

                                <ReactMapGL
                                    mapboxAccessToken='pk.eyJ1Ijoid2FmaWNraCIsImEiOiJjbGNuZ2ttcnoxMm83M25zOTM0cGpkanlmIn0.8U0pDk8RKlhvQrxzgWsbxQ'
                                    {...viewport}
                                    onMove={(newView) => setViewport(newView)}
                                    style={{ width: 340, height: 400, marginTop: 10, marginRight: 10, marginLeft: -25 }}
                                    mapStyle="mapbox://styles/mapbox/streets-v12"
                                    onClick={add_marker}

                                >
                                    <Marker longitude={marker.longitude}
                                        latitude={marker.latitude}></Marker>
                                
                                </ReactMapGL>

                            </div>
                        </div>
                        <div className="buttons">
                            <button className='back'>&laquo; Back</button>
                            <button type='submit' className='continue'>Continue &raquo;</button>

                        </div>
                    </form>
                </div>
            </div>
        </span>
    )
}

export default AddDorm
