import React, { useState, useEffect } from 'react'
import { Link, useNavigate, useLocation, Navigate } from "react-router-dom"
import * as actionType from '../../constants/actionTypes';
import { useDispatch } from 'react-redux';
import decode from 'jwt-decode';
import Navbar from '../../components/navbar/Navbar'
import './Admin.css'
export default function Admin() {
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    const dispatch = useDispatch();
    const location = useLocation();
    const history = useNavigate();


    const logout = () => {
        dispatch({ type: actionType.LOGOUT });
        setUser(null);
        history.push('/auth');


    };

    useEffect(() => {
        if (!user || user.result.email !== "wafic@hotmail.com") {
            history('/auth')
        }


    }, [location]);

    return (
        <div>

            <Navbar />
            {user.result.email === "wafic@hotmail.com" ? (
                <><div className='flexx'>
                    <div>
                        navigation stuff go here
                        <h2>Get Users (delete users)</h2>
                        <h2>Get Dorms (display dorms that are not yet approved</h2>
                        <h2>Get Reviews</h2>
                        

                    </div>
                    <div>
                        display the actual data
                    </div>



                </div></>
            ) : (<>
               <center><h1>This Page does not exist</h1></center> 
            </>)}



        </div>
    )
}
