import React, { useState, useEffect ,useDispatch} from 'react'
import "./Navbar.css"
import { Link, useNavigate, useLocation } from "react-router-dom"
import * as actionType from '../../constants/actionTypes';

function Navbar() {
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    const dispatch = useDispatch();
    const location = useLocation();
    const history = useNavigate();


    const logout = () => {
        dispatch({ type: actionType.LOGOUT });

        history.push('/auth');

        setUser(null);
    };

    useEffect(() => {
        const token = user?.token;

        if (token) {
          //  const decodedToken = decode(token);

          //  if (decodedToken.exp * 1000 < new Date().getTime()) logout();
        }

        setUser(JSON.parse(localStorage.getItem('profile')));
    }, [location]);

    return (
        <nav className="navo ">
            <div className="full">

                <div className="wogo">
                    <div className="w">
                        <a component={Link} to="/" class="navLogo" >
                            <Link to="/"> <span className='logoo'>
                                Dorm Critique</span></Link>
                        </a>
                    </div>

                    <div className="sign">
                        {user ? (
                            <>
                                <a className="link" href='/'>
                                    <span className="sign" style={{ color: '#ff6c02' }}> Dashboard</span>
                                </a>
                                <a className="link" href='/'>
                                    <span className="sign" style={{ color: '#ff6c02' }}> Logout</span>
                                </a>
                            </>

                        ) : (
                            <a className="link" component={Link} to="/auth">
                                <Link to="/auth"> <span className="sign" style={{ color: '#ff6c02' }}> Sign in</span></Link>
                            </a>
                        )}

                    </div>
                </div>
            </div>




        </nav>
    )
}

export default Navbar
