import React from 'react'
import Footer from '../../components/footer/Footer'
import Navbar from '../../components/navbar/Navbar'
import "./AddDorm.css"
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup"
import * as yup from "yup";
import { useJsApiLoader } from "@react-google-maps/api"
//Api key: AIzaSyBrbOMMdxU0mBqQXscT_FNBZBKm6CABzMU
import { SkeletonText } from "@material-ui/core"
const schema = yup.object().shape({
    DormName: yup.string().min(3).max(90)
        .required('This field is required')
})


function AddDorm() {
    const { isLoaded } = useJsApiLoader({
        googleMapsApiKey: AIzaSyBrbOMMdxU0mBqQXscT_FNBZBKm6CABzMU,
    })
    if (!isLoaded) {
        return <p>is loading...</p>
    }
    const { register, handleSubmit, errors } = useForm({
        resolver: yupResolver(schema),
    });

    const submitForm = (data) => {
        console.log(data);
    };
    return (
        <span className='bud'>
            <Navbar />
            <div className="bod">
                <div className='card card7'>
                    <form action="" onSubmit={handleSubmit(submitForm)}>
                        <div className='head'>
                            <h1>Add a dorm</h1>
                            <p>Enter the full name and Location of the dorm  you would like to add.</p>
                        </div>
                        <div className="inp">
                            <h4>Dorme name</h4>
                            <input type="text" name="DormName" id="Dormname" placeholder='Dorm name' className='form-control form-input shadow-none rounded-3 pad' ref={register} />

                            <span className='error'>{errors.DormName?.message}</span>

                        </div>
                        <div className="location">
                            <h4>Please Select the exact location of your dorm.</h4>
                            <div className="Map">Very cool google map </div>
                        </div>
                        <div className="buttons">
                            <button className='back'>&laquo; Back</button>
                            <button type='submit' className='continue'>Continue &raquo;</button>

                        </div>
                    </form>
                </div>
            </div>
        </span>
    )
}

export default AddDorm
